private async Task<T> getAsync<T>(string url, IEnumerable<KeyValuePair<string, string>> query) {
  using (HttpResponseMessage response = await httpClient.GetAsync(url + "?" + NewQueryString(query))) {
    try {
      response.EnsureSuccessStatusCode();
      using (Stream stream = await response.Content.ReadAsStreamAsync()) {
        return fromJson<T>(stream);
      }
    } catch (HttpRequestException ex) {
      // Обработка исключений, связанных с запросом
      if (ex.StatusCode == System.Net.HttpStatusCode.Unauthorized) {
        // Неверный токен 
        throw new InvalidOperationException("Неверный токен доступа!", ex); 
      } else {
        // Другие ошибки запроса
        throw; // Переброс исключения
      }
    } catch (JsonException ex) {
      // Обработка исключений, связанных с JSON
      throw new InvalidCastException("Неправильный формат JSON!", ex);
    }
  }
}

private string NewQueryString(IEnumerable<KeyValuePair<string, string>> query) {
  StringBuilder sb = new StringBuilder();
  bool first = true;
  foreach (var pair in query) {
    if (first) {
      first = false;
    } else {
      sb.Append("&");
    }
    sb.Append(pair.Key).Append("=").Append(pair.Value);
