  using (var response = await httpClient.GetAsync(url + buildQueryString(query))) {
    // Обработка ошибок HTTP
    if (!response.IsSuccessStatusCode) {
      throw new HttpRequestException("ОШИБКА HTTP: " + response.StatusCode);
    }

    using (var stream = await response.Content.ReadAsStreamAsync()) {
      try {
        return fromJson<T>(stream);
      } catch (JsonException ex) {
        throw new ArgumentException("Не удалось перевести JSON в объект T: " + ex.Message, ex);
      }
    }
  }
}

private string buildQueryString(IEnumerable<KeyValuePair<string, string>> query) {
  if (query.Count() == 0) {
    return string.Empty;
  }

  return "?" + string.Join("&", query.Select(kvp => $"{Uri.EscapeDataString(kvp.Key)}={Uri.EscapeDataString(kvp.Value)}"));
}
